<script id="slotViewerTemplate" type="text/template">

    <form class="slot-viewer" method="GET" action="https://www.practo.com/health/appointment/book" >
        <input type="hidden" value="<%= abs_enabled ? 'abs' : 'request' %>" name="type"/>
        <input type="hidden" value="<%-doctor.id%>" name="doctor_id"/>
        <div class="notch"></div>
        <div class="sep-shadow-top"></div>
        <div class="prev-day">
            <a class="button day-switcher prev disabled" data-id="0">PREV DAY</a>
            <div class="clear"></div>
        </div>

        <div class="slot-header">
            <div class="morning">MORNING</div>
            <div class="afternoon">AFTERNOON</div>
            <div class="evening">EVENING</div>
            <div class="night">NIGHT</div>
        </div>
        <% var dayIndex = 0; _.each(daySlots,function(thisDay){
            validSlotsForDay = validSlots(thisDay.slots,thisDay.day,doctor.visit_timings);
            hasAtleastOneBookableSlot = _.find(validSlotsForDay,function(thisSlot) { return !thisSlot.outsideVisitHours});
            if (hasAtleastOneBookableSlot){%>
            <div class="slots-group" <%= dayIndex > 2 ? 'style="display:none;"' : '' %>>
                <div class="day-row">
                    <div class="day-label">
                        <% if (thisDay.relDay != null) { %>
                        <div class="rel-day"><%=thisDay.relDay%></div>
                        <% } %>
                        <div class="week-day"><%=thisDay.day%></div>
                        <div class="date"><%=thisDay.date%></div>
                    </div>
                    <div class="slot-header inside-group">
                        <div class="morning">MORNING</div>
                        <div class="afternoon">AFTERNOON</div>
                        <div class="evening">EVENING</div>
                        <div class="night">NIGHT</div>
                    </div>

                    <% temp = _.groupBy(validSlotsForDay, function(thisSlot){
                        slotVal = parseInt(thisSlot.ts.substr(11,5).split(':').join(''),10);

                        limiters = [0,930,1130,1330,1530,1730,1930];
                        limit = 0;

                        _.each(limiters, function(limiter){
                            if(slotVal > limiter){
                                limit = limiter;
                            }
                        });

                        return limit;
                    }); %>
                    <%_.each(temp, function(thisSlotGroup){ %>

                    <div class="day-slot-column">
                        <% _.each(thisSlotGroup, function(thisSlot){ %>
                        <% if(! thisSlot.outsideVisitHours) {%>
                        <input type="radio" <%=thisSlot.available ? '' : 'disabled="disabled"'%> value="<%=thisSlot.ts%>" name="appointment_time" id="slot_<%-doctor.id%><%=(typeof doctor['ad'] != 'undefined') ? ('_' + doctor.ad.id) : ''%>_<%=replaceAll(replaceAll(thisSlot.ts,':',''),' ','')%>"></input>
                        <label class="book-slot" for="slot_<%-doctor.id%><%=(typeof doctor['ad'] != 'undefined') ? ('_' + doctor.ad.id) : ''%>_<%=replaceAll(replaceAll(thisSlot.ts,':',''),' ','')%>"><%=mysqlTSToDate(thisSlot.ts,false).format("h:MM TT")%></label>
                        <%}%>
                        <% }) %>
                    </div>
                    <% }) %>
                </div>
            </div>
            <% dayIndex ++;
        }}); %>
        <div class="clear"></div>
        <div class="action-panel rightalign">
            <a class="button day-switcher next">NEXT DAY</a>
            <input type="hidden" name="appointment_token" value="<%=appointment_token%>"/>
            <% if (typeof adReferral != "undefined") { %>
            <input type="hidden" name="ad_id" value="<%=adReferral.ad_id%>"/>
            <input type="hidden" name="ad_slot_position" value="<%=adReferral.position%>"/>
            <input type="hidden" name="ad_referrer" value="<%=adReferral.source%>"/>
            <% } else
            if (typeof doctor["ad"] != "undefined") { %>
            <input type="hidden" name="ad_id" value="<%=doctor.ad.id%>"/>
            <% } %>

            <% if (!abs_enabled) { %>
            <div class="non-abs-disclaimer">
                <p>This is <span class="strong">only</span> an appointment request.</p>
                <p>To get instant confirmation, book an appointment with <span class="suggest_abs_icon"></span>&nbsp;<span class="strong">ABS</span> <%=doctor.labels.doctor%>s</p>
            </div>
            <% } %>
        </div>
    </form>

</script>

